import React from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import data from './data.json';
import './Cinemas.css'; // Create a CSS file for styling if not already present

const Cinemas = () => {
  const { homeData } = data;

  const settings = {
    // settings for the slider...
  };

  return (
    <div className='Home'>
      <Slider {...settings}>
        {homeData.map((cinema) => (
          <div key={cinema.id}>
            <h3>{cinema.name}</h3>
            <p>{cinema.description}</p>
            <img
              src={cinema.url}
              alt={cinema.name}
              className='slider-image'
            />
          </div>
        ))}
      </Slider>
    </div>
  );
};

export default Cinemas;
