import data from './data.json';
import '../App.css'

const Films = () => {
  const {moviesData}=data
  const[filter,setFilter]=useState('')

  // const handleButton =()=>{
  //   alert("You should login to buy ticket")
  // }
  return (
    <div className='container'>
      <div className="search-btn">
      {data.moviesData.map((item,index)=>{
         return
         
        })}
     <input type="text" placeholder='Search...' className='inputText'/>
     <button><i className="fas fa-search "></i></button>
     </div>
    <div className='list'>
      {moviesData.map((movies)=>(
        <div key={movies.id}>
          <div className='listItem'>
            <img 
            src={movies.url}
            alt={movies.name} className='items'/>
         
          <div className='text'>
            <h3>{movies.name}</h3>
            <span>{movies.duration}</span>
            <button className='buyButton' >
            <i className="fa fa-check-double"></i>Buy
            </button>
          </div>
        </div>
        </div>
   
      ))}
    </div>
      
    </div>
  )
}

export default Films
