  import React,{useState} from 'react'
import { useForm } from 'react-hook-form';
  import '../App.css';
  import Films from './films'
  import '../components/Navbar.css'

  export default function SignIn(){

    const {register,handleSubmit} =useForm()
    const onSubmit=data=>console.log(data)

    const handleClick=()=>{
      return <Films/>
    }

      return(
        <section>
          <div className='wrapper'>
          <form action="" onSubmit={handleSubmit(onSubmit)} >
          <h1>Sign Up</h1>
          <div className='input-box'>
          <input {...register("firstname")} name='firstname' type='text' placeholder="First Name"  />
          
          <input  {...register("lastname")} name='lastname' type='text' placeholder="Last Name"  />

          </div>
          <div className='input-box' >
          <input {...register("username")} name='username' type='text' placeholder="Username"  />
          
          <input {...register("password")} name='password' type='password' placeholder="password" />
          </div>
        

          <button type="submit" onclick={handleClick}>Sign Up</button>
          <div className='register'>
              <p>Already a member? <a href="signIn">LogIn</a></p>
          </div>
          
          
          
      </form>
      </div>
      </section>
      )
      }
