import React,{useState} from 'react'
import data from './data.json'

const {users}=data

const SignIn=()=>{
    const [formData,setFormData]=useState({
        username:"",
        password:""
    })
    const [loggedInUser,setLoggedInUser]=useState(null)
    const [isError,setIsError]=useState(null)
   
    const handleOnChange=(e)=>{
        const{value,name}=e.target
        setFormData((prevState)=>{
            return{
                ...prevState,
                [name]:value
            }
        })
    }

    const handleLogin=(e)=>{
        e.preventDefault()
        const {username,password}=data
        if(username.length<2){
            console.log("Username is too short")
            return;
        }
        if(password.length<4){
            console.log("Password is too short")
            return;
        }
        data.login({username,password}).then((res)=>setLoggedInUser(res)).catch((error)=>setIsError(error))
    }
    if(loggedInUser){
        return<div>
            <h1>Welcome back, {loggedInUser.name}</h1>
        </div>
    }
    return <form onSubmit={handleLogin}>
        <input value={formData.username} name='username' type='text' placeholder="Username" onChange={handleOnChange}/>
        <input value={formData.password} name='password' type='password' placeholder="password" onChange={handleOnChange}/>
        {
            isError && <p style={{color:'tomato'}}>{isError}</p>
        }
        <button type="submit">Login</button>
        
    </form>

}
export default SignIn;
