import React,{useState} from 'react'
import signInAPI from './signInAPI'



export default function SignIn(){
    const [formData,setFormData]=useState({
        username:"",
        password:""
    })
    const [loggedInUser,setLoggedInUser]=useState(null)
    const [isError,setIsError]=useState(null)
   
    const handleOnChange=(e)=>{
        const{value,name}=e.target
        setFormData((prevState)=>{
            return{
                ...prevState,
                [name]:value
            }
        })
    }

    const handleLogin=(e)=>{
        e.preventDefault()
        const {username,password}=formData
        if(username.length<2){
            console.log("Username is too short")
            return;
        }
        if(password.length<4){
            console.log("Password is too short")
            return;
        }
        signInAPI.login({username,password}).then((res)=>setLoggedInUser(res)).catch((error)=>setIsError(error))
    }
    if(loggedInUser){
        return<div>
            <h1>Welcome back, {loggedInUser.name}</h1>
        </div>
    }
    return <form action="" onSubmit={handleLogin}>
        <div className='input-box'>
        <input value={formData.username} name='username' type='text' placeholder="Username" onChange={handleOnChange}/>
        </div>
        <div className='input-box'>
        <input value={formData.password} name='password' type='password' placeholder="password" onChange={handleOnChange}/>
        </div>
   
        {
            isError && <p style={{color:'tomato'}}>{isError}</p>
        }
        {/* <div className='remember-forgot'>
         
                <input type="checkbox">Remember me</input>
            <a href="#">Forgot password?</a>
        </div>
        <button type="submit">Login</button>
        <div className='register'>
            <p>Don't have an account?<a href="#">Register</a>
            </p>
        </div> */}
        
    </form>

}
