export default {
  login: data => {
    const { username, password, firstname, lastname } = data; // Retrieve first and last names from input data
    return new Promise((resolve, reject) => {
      if (username === "bleronatmava" && password === "123456") {
        resolve({
          username,
          firstname: firstname || '', 
          lastname: lastname || '', 
          name: "Blerona",
          surname: "Tmava",
          lastLogin: new Date("04/08/2019 13:20:22")
        });
      } else {
        reject("Incorrect password or login!");
      }
    });
  }
};
