import React, {useState} from 'react'
import {useForm} from 'react-hook-form';
import '../App.css';
import '../components/Navbar.css';
import Films from './films';

export default function SignIn() {

    const {register, handleSubmit} = useForm()
    
    const onSubmit = data => console.log(data)

       function checkFirstName(){
        const firstname=document.getElementById("values").value;
        if(!firstname){
            alert("Please fill ")
        }
        else return <Films/>
       }

    return (
        <section>
            <div className='wrapper'>
                <div
                    className='form'
                    style={{
                    height: 620
                }}>
                    <form action="" onSubmit={handleSubmit(onSubmit)}>
                        <h1>Sign Up</h1>
                        <div className='input-box'>
                            <input
                                {...register("firstname")}
                                name='firstname'
                                type='text' id='values'
                                placeholder="First Name"/>
                        </div>
                        <div className='input-box'>
                            <input
                                {...register("lastname")}
                                name='lastname'
                                type='text' id='values'
                                placeholder="Last Name"/>

                        </div>
                        <div className='input-box'>
                            <input
                                {...register("username")}
                                name='username'
                                type='text' id='values'
                                placeholder="Username"/>
                        </div>
                        <div className='input-box'>
                            <input
                                {...register("password")}
                                name='password'
                                type='password'id='values'
                                placeholder="password"/>
                        </div>

                        <button type="submit"onclick="checkFirstName()" >Sign Up</button>
                        <div className='register'>
                            <p>Already a member?
                                <a href="signIn">LogIn</a>
                            </p>
                        </div>

                    </form>
                </div>
            </div>
        </section>
    )
}
