import React,{useState} from 'react'
import signInAPI from './signInAPI'
import '../App.css';
import Films from './films'
import '../components/Navbar.css'

export default function SignIn(){
    // const [firstname,setFirstname]=useState('');
    // const [lastname,setLastname]=useState('');
    // const [username,setUsername]=useState('');
    // const [pass,setPass]=useState('');

    const [formData,setFormData]=useState({
      username:"",
      password:"",
      firstName:"",
      lastName:""
  })
  const [loggedInUser,setLoggedInUser]=useState(null)
  const [isError,setIsError]=useState(null)
 
  const handleOnChange=(e)=>{
      const{value,name}=e.target
      setFormData((prevState)=>{
          return{
              ...prevState,
              [name]:value
          }
      })
      console.log(value)
  }
   const handleLogin=(e)=>{
        e.preventDefault()
        const {firstName,lastName,username,password}=formData
       
        signInAPI.login({firstName,lastName,username,password}).then((res)=>setLoggedInUser(res)).catch((error)=>setIsError(error))
    }
    if(loggedInUser){
        return<div>
           <Films/>
        </div>
    }

    return(
      <div className='wrapper'>
      <form action="" onSubmit={handleLogin}>
      <h1>LogIn</h1>
      <div className='input-box'>
      <input value={this.state.firstName} name='FirstName' type='text' placeholder="FirstName" onChange={(e) => this.setState({ firstName: e.target.value })} />
      <input value={this.state.lastName} name='LastName' type='text' placeholder="LastName" onChange={(e) => this.setState({ lastame: e.target.value })}/>
 
      </div>
      <div className='input-box'>
      <input value={formData.username} name='username' type='text' placeholder="Username" onChange={handleOnChange} />
    
      <input value={formData.password} name='password' type='password' placeholder="password" onChange={handleOnChange}/>
 
      </div>
      <div className='remember'>
      <label><input type="checkbox" />Remember</label>
      <a href='#'>Forgot password</a>

      </div>
      <button type="submit">Login</button>
      <div className='register'>
          <p>Don't have an account? <a href="signUp">Register</a></p>
      </div>
      
  </form>
  </div>
    )
    }
