import React from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import { homeData } from './data.json';

const Cinemas = () => {
  // Settings for the Slick Slider
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 2000,
  };

  return (
    <div className='Home'>
      <Slider {...settings}>
        {homeData.map((cinema) => (
          <div key={cinema.id}>
            <h3>{cinema.name}</h3>
            <p>{cinema.description}</p>
            {/* Render other cinema details here */}
            <img src={cinema.url} alt={cinema.name} />
          </div>
        ))}
      </Slider>
    </div>
  );
};

export default Cinemas;
