import React from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import data from './data.json';
import './Cinema.css'

const Cinemas = () => {
  const { homeData } = data;

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1, 
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 2000,
    // responsive:[
    //   {
    //     breakpoint:1024,
    //     setting:{
    //       slidesToShow:1,
    //     },
    //   },
    //   {
    //     breakpoint:768,
    //     setting:{
    //       slidesToShow:1,
    //     },
    //   },
    // ]
  };

  return (
    <div className='Home'>
      <Slider {...settings}>
        {homeData.map((cinema) => (
          <div key={cinema.id}>
            <img
              src={cinema.url}
              alt={cinema.name}
              className='slider-image'
            />
          </div>
        ))}
      </Slider>
    </div>
    
  );
};

export default Cinemas;
