import React,{useState} from 'react'
import signInAPI from './signInAPI'
import '../App.css';
import Films from './films'
import '../components/Navbar.css'

export default function SignIn(){
    const [firstname,setFirstname]=useState('');
    const [lastname,setLastname]=useState('');
    const [username,setUsername]=useState('');
    const [pass,setPass]=useState('');

    const [loggedInUser,setLoggedInUser]=useState(null)
    const [isError,setIsError]=useState(null)
  // const handleSubmit=(e)=>{
  //   e.preventDefault();
  //   console.log(username);
  // }
  const handleSubmit=(e)=>{
    e.preventDefault()
    const {username,password}=formData
    if(username.length<2){
        console.log("Username is too short")
        return;
    }
    if(password.length<4){
        console.log("Password is too short")
        return;
    }
    signInAPI.login({username,password}).then((res)=>setLoggedInUser(res)).catch((error)=>setIsError(error))
}
if(loggedInUser){
    return<div>
       <Films/>
    </div>
}

    return(
         <div className='wrapper'>
        <form action="" onSubmit={handleSubmit} >
        <h1>Sign Up</h1>
        <div className='input-box'>
        <input value={firstname} onChange={(e)=>setFirstname(e.target.value)} name='firstname' type='text' placeholder="First Name"  />
        
        <input  value={lastname} onChange={(e)=>setLastname(e.target.value)} name='lastname' type='text' placeholder="Last Name"  />

        </div>
        <div className='input-box'>
        <input value={username} onChange={(e)=>setUsername(e.target.value)} name='username' type='text' placeholder="Username"  />
        
        <input value={pass} onChange={(e)=>setPass(e.target.value)} name='password' type='password' placeholder="password" />
        </div>

        <button type="submit">Sign Up</button>
        <div className='register'>
            <p>Already a member? <a href="signIn">LogIn</a></p>
        </div>
        
        
        
    </form>
    </div>
    )
    }
