import React,{useState} from 'react'
import signInAPI from './signInAPI'
import '../App.css';
import Films from './films'
import '../components/Navbar.css'

export default function SignIn(){
  const [formData,setFormData]=useState({
    firstname:"",
    lastname:"",
    username:"",
    password:""
})
const [loggedInUser,setLoggedInUser]=useState(null)
const [isError,setIsError]=useState(null)

const handleOnChange=(e)=>{
    const{value,name}=e.target
    setFormData((prevState)=>{
        return{
            ...prevState,
            [name]:value
        }
    })
}
  const handleLogin=(e)=>{
    e.preventDefault()
    const {username,password}=formData
    if(username.length<2){
        console.log("Username is too short")
        return;
    }
    if(password.length<4){
        console.log("Password is too short")
        return;
    }
    signInAPI.login({username,password}).then((res)=>setLoggedInUser(res)).catch((error)=>setIsError(error))
}
if(loggedInUser){
    return<div>
       <Films/>
    </div>
}
    

    return(
         <div className='wrapper'>
        <form action="" onSubmit={handleLogin}>
        <h1>Sign Up</h1>
        <div className='input-box'>
        <input value={formData.firstname} name='firstname' type='text' placeholder="First Name"  />
        
        <input value={formData.lastname} name='lastname' type='text' placeholder="Last Name"  />

        </div>
        <div className='input-box'>
        <input value={formData.username} name='username' type='text' placeholder="Username"  />
        
        <input value={formData.password} name='password' type='password' placeholder="password" />
        </div>

        <button type="submit">Sign Up</button>
        <div className='register'>
            <p>Already a member? <a href="signIn">LogIn</a></p>
        </div>
        
        
        
    </form>
    </div>
    )
    }
